import React from 'react';
import classNames from 'classnames';
import { SectionSplitProps } from '../../utils/SectionProps';
import SectionHeader from './partials/SectionHeader';
import Image from '../elements/Image';

const propTypes = {
  ...SectionSplitProps.types
}

const defaultProps = {
  ...SectionSplitProps.defaults
}

const FeaturesSplit = ({
  className,
  topOuterDivider,
  bottomOuterDivider,
  topDivider,
  bottomDivider,
  hasBgColor,
  invertColor,
  invertMobile,
  invertDesktop,
  alignTop,
  imageFill,
  ...props
}) => {

  const outerClasses = classNames(
    'features-split section',
    false && 'has-top-divider',
    false && 'has-bottom-divider',
    hasBgColor && 'has-bg-color',
    invertColor && 'invert-color',
    className
  );

  const innerClasses = classNames(
    'features-split-inner section-inner',
    false && 'has-top-divider',
    false && 'has-bottom-divider'
  );

  const splitClasses = classNames(
    'split-wrap',
    invertMobile && 'invert-mobile',
    invertDesktop && 'invert-desktop',
    alignTop && 'align-top'
  );

  const sectionHeader = {
    title: '',
    paragraph: ''
  };

  return (
    <section
      {...props}
      className={outerClasses}
    >
      <div className="container">
        <div className={innerClasses}>
          <SectionHeader data={sectionHeader} className="center-content" />
          <div className={splitClasses}>

            <div className="split-item">
              <div className="split-item-content center-content-mobile reveal-from-left" data-reveal-container=".split-item">
                <div className="text-xs text-color-primary fw-600 tt-u mb-8">
                  Meet your tutor  
                </div>
                <h3 className="mt-0 mb-12">
                  David Hasani
                  </h3>
                <p className="m-0">
                  Issue your very own tokens to obtain funds needed to level up as a performer or grow your business.
                  Then, leverage a strong, loyal fanbase to further expand & promote your token â€” sending its price to the moon!<span role="img" aria-label="rocket"> ðŸš€</span>
                  </p>
              </div>
              <div className={
                classNames(
                  'split-item-image center-content-mobile reveal-from-bottom',
                  imageFill && 'split-item-image-fill'
                )}
                data-reveal-container=".split-item">
                <Image
                  src={require('./../../assets/images/creator.png')}
                  alt="Features split 01"
                  width={528}
                  height={396} />
              </div>
            </div>

            <div className="split-item">
              <div className="split-item-content center-content-mobile reveal-from-right" data-reveal-container=".split-item">
                <div className="text-xs text-color-primary fw-600 tt-u mb-8">
                  Fund the next superstars
                  </div>
                <h3 className="mt-0 mb-12">
                  Investors
                  </h3>
                <p className="m-0">
                  Purchase tokens of your favorite talent to support their growth and development.
                  Then, exchange some tokens for
                  exclusive perks (think merchandise discounts and autographed items), and sell your tokens for a
                  huge profit in the future.
                  </p>
              </div>
              <div className={
                classNames(
                  'split-item-image center-content-mobile reveal-from-bottom',
                  // imageFill && 'split-item-image-fill'
                )}
                data-reveal-container=".split-item">
                <Image
                  src={require('./../../assets/images/investor.png')}
                  alt="Features split 02"
                  width={396}
                  height={297} />
              </div>
            </div>

          </div>
        </div>

      </div>
    </section>
  );
}

FeaturesSplit.propTypes = propTypes;
FeaturesSplit.defaultProps = defaultProps;

export default FeaturesSplit;
